%% local functions


function ExtSetPktWithACK(pktData)

pktSize           = length(pktData);         % too long for FIFO buffer
totalBytesWritten = 0;

% test ExtAdjPktSize
while (totalBytesWritten < pktSize)
    
    % adjust package size to ensure that only entire log data records
    % / packages are being sent
    adjPktSize = ExtAdjPktSize(pktSize, totalBytesWritten, pktData);
    
    % Send packet / the next section of the entire packet
    disp('------------------------------------------------------------------------------------------------')
    disp(pktData(totalBytesWritten + 1:totalBytesWritten + adjPktSize))
    disp('------------------------------------------------------------------------------------------------')
    
    % adjust number of bytes which have been sent so far
    totalBytesWritten = totalBytesWritten + adjPktSize;
    
end
